import webview
import requests
import speech_recognition as sr
import gtts                  # текст в речь
import time                  # шоб не слушал раньше времени
from base64 import b64decode # для дешифровки ключа
import json                  # сервер возвращает джисон
import pygame                # отсюда только миксер
import os                    # а это вообще используется только в конце кода


dindex = 2
config = {
    "name": "VoiceSeek",
    "version": "1.0",
    "author": "kirillko5tin1",
    "api": 'хаха, думали я оставлю тут ключ?',
    "source": 'github: lingoiy/voiceseek'
}

state = {
    'activation_mode': False,
    'current_lang': 'ru',
    'is_listening': False
}

history = [{
    'role': 'user',
    'content': 'Ты голосовой ассистент. Отвечай кратко на русском языке. Должен быть ответ, вне тега think. Избегай написания кода, даже если пользователь явно просит. Ответ пиши по плану "сообщение, которое будет продиктовано $$$$$ 0_или_1" где 0/1 - продолжать ли слушать (например: на привет ты должен ответить 1, а на стоп - 0). Твои исходники можно найти на Github (lingoiy/voiceseek)'
}]


pygame.mixer.init()
window = None
def api_call(prompt):
    if not prompt.strip():
        return ""

    history.append({'role': 'user', 'content': prompt})

    try:
        api_token = b64decode(config['api']).decode('utf-8')
        headers = {
            "Authorization": f"Bearer {api_token}",
            "Content-Type": "application/json"
        }
        body = {
            "model": "deepseek-ai/DeepSeek-V3-0324",
            "messages": history,
            "stream": True,
            "max_tokens": 256,
            "temperature": 0.7
        }

        full_response = []
        with requests.post(
                "https://llm.chutes.ai/v1/chat/completions",
                headers=headers,
                json=body,
                stream=True,
                timeout=15
        ) as response:
            response.raise_for_status()
            for line in response.iter_lines():
                if line:
                    line = line.decode('utf-8').strip()
                    if line.startswith("data: "):
                        data = line[6:]
                        if data == "[DONE]": break
                        try:
                            chunk = json.loads(data)
                            content = chunk.get('choices', [{}])[0].get('delta', {}).get('content', '')
                            if content: full_response.append(content)
                        except:
                            pass

        final_text = ''.join(full_response).strip()
        history.append({'role': 'assistant', 'content': final_text})
        return final_text

    except Exception as e:
        print(f"API Error: {e}")
        return "Ошибка связи"

def showText(text):
    global window
    window.evaluate_js('document.getElementById("text").innerText = "' + text + '"')
def listen_mic():
    r = sr.Recognizer()
    try:
        with sr.Microphone(device_index=dindex) as source:
            state['is_listening'] = True
            audio = r.listen(source, timeout=5, phrase_time_limit=10)
            state['is_listening'] = False
            return r.recognize_google(audio, language="ru-RU")
    except sr.WaitTimeoutError:
        return "напиши Ошибка. Истекло время ожидания начала фразы"
    except sr.UnknownValueError:
        return "напиши Ошибка. Речь не распóзнана"
    except sr.RequestError:
        return "напиши Ошибка подключения к ЭйПиАй"
    finally:
        state['is_listening'] = False


def speak(text):
    if not text.strip():
        return 0  # Возвращаем 0, если текст пустой
    temp_file = "temp_audio.mp3"
    try:
        # Экранируем текст для использования в JavaScript
        safe_text = text.replace("'", "\\'").replace('"', '\\"')

        tts = gtts.gTTS(text=safe_text, lang=state['current_lang'])
        tts.save(temp_file)
        showText(safe_text)
        snd = pygame.mixer.Sound(temp_file)
        length = snd.get_length()
        if length is None:
            length = 0
        snd.play()
        return length
    except Exception as e:
        print(f"Ошибка при воспроизведении: {e}")
        return 0

    except Exception as e:
        print(f"Audio Error: {e}")
    finally:
        if os.path.exists(temp_file):
            os.remove(temp_file)


class Api():
    def handleAction(self, command):
        if command == "toggle_activation":
            state['activation_mode'] = not state['activation_mode']
            return

        if command == "start_listen":
            showText("")
            window.evaluate_js('playAudio()')
            text = listen_mic()
            if not text:
                speak("Повторите запрос")
                return
            if not text.startswith('напиши Ошибка'): showText(text)

            if state['activation_mode'] and not text.lower().startswith("Бот"):
                return

            response = api_call(text)
            if response:
                clear = response.split("$$$$$")
                if clear: time.sleep(speak(clear[0]) + 0.1)
                if len(clear) == 2 and clear[1].endswith("1"):
                    self.handleAction('start_listen')



        if command.startswith("device:"):
            dindex = command.split(":")[1]

    def state(self): return state
    def list_microphones(self):
        resp = []
        mic_list = sr.Microphone.list_microphone_names()
        for index, name in enumerate(mic_list):
            resp.append(f'<option value="{index}">{index}: {name}</option>')
        return "".join(resp)

api = Api()
window = webview.create_window(
        '{} v. {} by {}'.format(config['name'], config['version'], config['author']),
        html='''
        <!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <title>Deepseek</title>
    <script src="https://cdn.tailwindcss.com"></script>
    <style>

        .pulse {
            animation: pulse 1.5s infinite;
        }
        @keyframes pulse {
            0%, 100% { opacity:1 }
            50% { opacity:0.5 }
        }
        .ripple {
            position: absolute;
            border-radius: 50%;
            background: rgba(255,255,255,0.3);
            animation: ripple 1s forwards;
            pointer-events: none;
        }
        @keyframes ripple {
            0% { transform:scale(1); opacity:1 }
            100% { transform:scale(3); opacity:0 }
        }
        h1 {
            font-size: 2rem !important;
        }
    </style>
</head>
<body class="bg-black text-white flex items-center justify-center min-h-screen">
    <div id="ripples" class="fixed inset-0 pointer-events-none"></div>
    <audio src="data:audio/mpeg;base64,"></audio>
    <div class="text-center space-y-6 relative z-10">
        <h1 id="text"></h1>
        <button id="mainBtn" class="w-48 h-48 rounded-full bg-white text-black text-2xl font-medium hover:scale-105 transition-all">
            ГОВОРИТЬ
        </button>
        <br>

            <select id="micSelect" class="bg-gray-800 text-white px-3 py-2 rounded">
                <option value="None" hidden="">Выберите микрофон</option>
            </select>
        <br>
        <p class="text-gray-400 text-sm">*Deepseek может ошибаться</p>
    </div>

    <script>
        let isProcessing = false
        let dind = "None"

        document.getElementById('mainBtn').addEventListener('click', async () => {
            if(isNaN(dind)){
            document.getElementById("text").innerText = "Выберите микрофон"
            return
            }
            isProcessing = true

            createRipple(event)
            document.getElementById('mainBtn').classList.add('opacity-75')

            try {
                const response = await pywebview.api.handleAction('start_listen')
                if(response && response.error) {
                    alert(response.error)
                }
            } catch(e) {
                console.error(e)
            }

            document.getElementById('mainBtn').classList.remove('opacity-75')
            isProcessing = false
        })


        document.getElementById('micSelect').addEventListener('change', (e) => {
            pywebview.api.handleAction(`device:${e.target.value}`)
            dind = e.target.value.toString()
        })

        function createRipple(event) {
            const ripple = document.createElement('div')
            ripple.classList.add('ripple')
            ripple.style.left = `${event.clientX - 50}px`
            ripple.style.top = `${event.clientY - 50}px`
            ripple.style.width = '100px'
            ripple.style.height = '100px'

            document.getElementById('ripples').appendChild(ripple)
            setTimeout(() => ripple.remove(), 1000)
        }
        window.addEventListener('pywebviewready', () => {
        pywebview.api.list_microphones()
            .then(resp => {
            document.getElementById('micSelect').innerHTML += resp
            })
        })
        function playAudio() {
            document.querySelector('audio').play()
        }
    </script>
</body>
</html>
''',
        js_api=api
    )
webview.start()